//   var cell2 = row.insertCell(1);
//   cell1.innerHTML = "NEW CELL1";
//   cell2.innerHTML = "NEW CELL2";
// }


  return (
    <div className="dashboard">
      <VerticalBar
        selectedOption={selectedOption}
        onSelectOption={setSelectedOption}
      />
      <div className="fid-logo">
      <img src={FIL}  />
      </div>
      <div className="center-view">
        <h2>User Public ID: {publicId}</h2>
        <h3>Account Balance: {accountBalance}</h3>

         <button type="button" onClick={SendCrypto}>Send Crypto</button>
        {/* <div
        className={`button ${selectedOption === 'send' ? 'active' : ''}`}
        onClick={() => onSelectOption('send')}
      > */}
        {/* Replace with your send logo */}
        {/* <img src={SEND} alt="Send Logo" /> */}
      {/* </div> */}


        {renderView()}
      </div>
    </div>
  );
};

export default Dashboard;
